
function getCookie(a){if(document.cookie.length>0){c_start=document.cookie.indexOf(a+"=");if(c_start!=-1){c_start=c_start+a.length+1;c_end=document.cookie.indexOf(";",c_start);if(c_end==-1)c_end=document.cookie.length;return unescape(document.cookie.substring(c_start,c_end))}}return 0}function setCookie(a,d,c){var b=new Date;b.setDate(b.getDate()+c);document.cookie=a+"="+escape(d)+(c==null?"":";expires="+b.toGMTString())+"; path=/"};

